script:
frappe.ui.form.on("Sales Invoice", {
  refresh(frm) {
    {
      frm.add_custom_button(
        "Crear Envio",
        function () {
          console.log("button clicked"); // clickea botón
         frappe.call({
    method: 'envios_erpnext.envios_erpnext.doctype.envio.envio.make_envios',
    args: {
        'customer': frm.doc.customer,
    },
    callback: function(r) {
        console.log(r.message);
        frappe.set_route(`envio/${r.message}`);
    }
});
        },
        "Create"
      );
    }
  },
});
